mapper.select:
SELECT t1.venue_city, t1.venue_other_service, t1.venue_charge, t1.venue_route, t1.venue_area, t1.venue_telephone, t1.venue_name, t1.venue_position, t1.venue_description, t1.venue_open_type, t1.business_hours, t1.venue_plan, t1.venue_panorama, t1.booking_tips, t1.venue_service, t1.venues_info_id FROM gen_venues_info t1 WHERE t1.is_delete = 0
AND ( t1.venue_name like CONCAT('%',CONCAT(#{keywords},'%')) or t1.venue_telephone like CONCAT('%',CONCAT(#{keywords},'%')) or t1.venue_description like CONCAT('%',CONCAT(#{keywords},'%')) )
AND t1.creator = #{creator}
AND t1.creator IN
#{data_creators[${index}]}
AND t1.venues_info_id IN
#{venuesInfoIds[${index}]}
GROUP BY t1.venue_city, t1.venue_other_service, t1.venue_charge, t1.venue_route, t1.venue_area, t1.longitude, t1.latitude, t1.venue_telephone, t1.venue_name, t1.venue_position, t1.venue_description, t1.venue_open_type, t1.business_hours, t1.venue_plan, t1.venue_panorama, t1.booking_tips, t1.venue_service, t1.venues_info_id

SELECT DISTINCT t1.venues_info_id, t1.venue_name, t1.venue_position, t1.longitude, t1.latitude, t1.venue_panorama, t1.venue_telephone FROM gen_venues_info t1 WHERE t1.is_delete = 0
AND t1.venue_type = #{venueType}
AND t1.venue_city = #{venueCity}
AND t1.venue_area = #{venueArea}
AND t1.venue_type = #{venueType}
AND ( t1.venues_name LIKE CONCAT('%',CONCAT(#{keyWords},'%')) )
ORDER BY t1.creator DESC
